import React, { useState } from "react";
import "./App.css";
import Form from "./Form";
import Grid from "./Grid";

function StudentDetails() {
  const [students, setStudents] = useState([]);
  const [editedStudent, setEditedStudent] = useState(null);

  const addStudent = (student) => {
    // Generate a unique ID for the new student
    const newStudent = { ...student, id: Date.now() };
    setStudents([...students, newStudent]);
  };

  const editStudent = (student) => {
    setEditedStudent(student);
  };

  const saveEditedStudent = (editedInfo) => {
    setStudents((prevStudents) =>
      prevStudents.map((student) =>
        student.id === editedStudent.id ? { ...student, ...editedInfo } : student
      )
    );
    setEditedStudent(null);
  };

  const deleteStudent = (student) => {
    setStudents(students.filter((s) => s.id !== student.id));
  };

  const filterStudentsAboveAge = (age) => {
    return students.filter((student) => student.age > age);
  };

  const filterStudentsBelowAge = (age) => {
    return students.filter((student) => student.age < age);
  };

  return (
    <div className="App">
      <h1>Student Management System</h1>
      <Form onSubmit={addStudent} />
      {editedStudent && (
        <Form onSubmit={saveEditedStudent} editedStudent={editedStudent} />
      )}
      <h2>Students</h2>
      <Grid students={students} onEdit={editStudent} onDelete={deleteStudent} />
      <h2>Students Above Age 20</h2>
      <Grid students={filterStudentsAboveAge(20)} />
      <h2>Students Below Age 20</h2>
      <Grid students={filterStudentsBelowAge(20)} />
    </div>
  );
}

export default StudentDetails;
